// SPDX-License-Identifier: MIT
// OpenZeppelin Contracts (last updated v5.0.0) (utils/Nonces.sol)
pragma solidity ^0.8.20;

/**
 * @dev Provides tracking nonces for addresses. Nonces will only increment.
 */
abstract contract Nonces {
    /**
     * @dev The nonce used for an `account` is not the expected current nonce.
     */
    error InvalidAccountNonce(address account, uint256 currentNonce);

    mapping(address account => uint256) private _nonces;

    /**
     * @dev Returns the next unused nonce for an address.
     */
    function nonces(address owner) public view virtual returns (uint256) {
        return _nonces[owner];
    }

    /**
     * @dev Consumes a nonce.
     *
     * Returns the current value and increments nonce.
     */
    function _useNonce(address owner) internal virtual returns (uint256) {
        // For each account, the nonce has an initial value of 0, can only be incremented by one, and cannot be
        // decremented or reset. This guarantees that the nonce never overflows.
        unchecked {
            // It is important to do x++ and not ++x here.
            return _nonces[owner]++;
        }
    }

    /**
     * @dev Same as {_useNonce} but checking that `nonce` is the next valid for `owner`.
     */
    function _useCheckedNonce(address owner, uint256 nonce) internal virtual {
        uint256 current = _useNonce(owner);
        if (nonce != current) {
            revert InvalidAccountNonce(owner, current);
        }
    }
}

contracts/lib/SignEIP712.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import {Order} from './Structs.sol';
import {EIP712} from '@openzeppelin/contracts/utils/cryptography/EIP712.sol';
import {ECDSA} from '@openzeppelin/contracts/utils/cryptography/ECDSA.sol';

/**
 * @title Sign
 * @dev Contains all of the order hashing functions for EIP712 compliant signature
 */
contract SignEIP712 is EIP712 {
      constructor(string memory name, string memory version) EIP712(name, version) {}
       bytes32 public constant ORDER_TYPEHASH =
    keccak256(
      'Order(address trader,uint8 side,address collection,uint256 tokenId,uint256 amount,address paymentToken,uint256 price,uint256 listingTime,uint256 expirationTime,uint256 salt'
    );

  function _hashOrder(Order calldata order, uint256 nonce) internal pure returns (bytes32) {
    return
      keccak256(
          abi.encode(
            ORDER_TYPEHASH,
            order.trader,
            order.side,
            order.collection,
            order.tokenId,
            order.amount,
            order.paymentToken,
            order.price,
            order.listingTime,
            order.expirationTime,
            order.salt,
            nonce
        )
      );
  }

  function _hashToSign(bytes32 orderHash) internal view returns (bytes32 hash) {
    return _hashTypedDataV4(orderHash);
  }

  function _verify(address signer, bytes32 digest, uint8 v, bytes32 r, bytes32 s) internal pure returns (bool) {
require(v == 27 || v == 28, "Invalid v parameter");
        address recoveredSigner = ecrecover(digest, v, r, s);
        if (recoveredSigner == address(0)) {
          return false;
        } else {
          return signer == recoveredSigner;
          }
  }
}

