contracts/PetMint.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.19;
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import {SaiyaPet721} from './SaiyaPet721.sol';
contract PetMint {
  uint256 public constant MAX_SUPPLY = 8888;
  uint256 public constant TIME_LIMIT = 10 minutes;
  uint256 public totalSupply = 0;
  uint256 public lastMintedAt = 0;
  IERC20 public SpToken;
  address public saiyaPet721;

  constructor(address _token, address _saiyaPet721) {
    SpToken = IERC20(_token);
    saiyaPet721 = _saiyaPet721;
  }

  function mint(address to) public {
    require(totalSupply < MAX_SUPPLY, 'Max supply reached');
    require(
      totalSupply < 100 || block.timestamp >= lastMintedAt + TIME_LIMIT,
      '15 minutes not passed since last mint'
    );
    totalSupply += 1;
    lastMintedAt = block.timestamp;
    SpToken.transfer(address(this), 100 * 10 ** 18);
    SaiyaPet721(saiyaPet721).mintPet(msg.sender);
  }
}

contracts/SaiyaPet721.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.19;

import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Pausable.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol';
import '@openzeppelin/contracts/access/manager/AccessManaged.sol';

import {Pet} from './game/Pet.sol';
import {SvgHelper} from './game/SvgHelper.sol';

contract SaiyaPet721 is Pet, ERC721, ERC721Pausable, ERC721Burnable, AccessManaged {
  uint256 private _nextTokenId;

  // initialAuthority：PetMint合约才能 mint
  constructor(address initialAuthority) ERC721('SaiyaPet', 'SP') AccessManaged(initialAuthority) {}

  function pause() public restricted {
    _pause();
  }

  function unpause() public restricted {
    _unpause();
  }

  // 挖出100之后， 10 分钟产出一个宠物
  function mintPet(address to) external restricted {
    uint256 tokenId = _nextTokenId++;
    _safeMint(to, tokenId);
    initPet(tokenId);
  }

  // The following functions are overrides required by Solidity.

  function _update(
    address to,
    uint256 tokenId,
    address auth
  ) internal override(ERC721, ERC721Pausable) returns (address) {
    return super._update(to, tokenId, auth);
  }

  function tokenURI(uint256 tokenId) public override view  returns (string memory) {
    if (ownerOf(tokenId) == address(0)) {
      return '';
    }
    return _bulidTokenURI(tokenId);
  }

  function _bulidTokenURI(uint256 tokenId) internal view returns (string memory) {
    if (pets[tokenId].level == 0) return SvgHelper.generateL0();
    if (pets[tokenId].level == 1) return SvgHelper.generateL1();
    if (pets[tokenId].level == 2) return SvgHelper.generateL2();
    return SvgHelper.generateL0();
  }

  function supportsInterface(bytes4 interfaceId) public view override(ERC721) returns (bool) {
    return super.supportsInterface(interfaceId);
  }
}

